definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout') {
            steps {
                git branch: 'main', credentialsId: 'git_credentials', url: 'https://github.com/opqdevops/java-tomcat-maven-example.git'
                sh "sleep 10"
            }
        }
        stage('Build') {
            steps {
               sh "mvn clean install"
               sh "sleep 10"
            }
        }
        stage('Deploy') {
            steps {
                deploy adapters: [tomcat9(credentialsId: 'tomcat_credentials', path: '', url: 'http://54.157.166.240:8080/')], contextPath: 'demo', war: '**/*.war'
                sh "sleep 10"
            }
        }
        stage('Test') {
            steps {
                echo "test cases will get executed."
                sh "sleep 10"
            }
        }
        stage('run-parallel-branches') {
  steps {
    parallel(
      a: {
        echo "This is branch a"
      },
      b: {
        echo "This is branch b"
      }
    )
  }
}
    }
}
